mapper.select:
select count(*)
from board
where board_status='Y' and board_type=#{i}

mapper.update:
update board
set board_count = board_count + 1
where board_no = #{bNo}

update board
set board_title = #{boardTitle}, board_content=#{boardContent}
where board_No= #{boardNo}

update board
set board_status = 'N'
where board_no = #{bId}

update reply
set re_status = 'N'
where re_no = #{replyNo}

update reply
set re_status = 'N'
where re_no = #{replyNo}
