mapper.update:
update volunteer
set V_MAIN_CATEGORY_NO = #{vMainCategoryNo}, V_SUB_CATEGORY_NO = #{vSubCategoryNo}, V_TARGET_CATEGORY_NO = #{vTargetCategoryNo},
	V_AREA = #{vArea}, V_NAME = #{vName}, V_CONTENT = #{vContent}, V_START_DATE = #{vStartDate}, V_END_DATE = #{vEndDate},
	V_START_TIME = #{vStartTime}, V_END_TIME = #{vEndTime}, V_RECRUITMENT_START_DATE = #{vRecruitmentStartDate}, V_RECRUITMENT_END_DATE = #{vRecruitmentEndDate},
	V_DAY = #{vDay}, V_COUNTALL = #{vCountAll}, V_LOCATION = #{vLocation}, V_ACTIVITY_TYPE = #{vActivityType}, V_CHARGE_NAME = #{vChargeName}, V_CHARGE_PHONE = #{vChargePhone}
where v_no = #{vNo}

update volunteer
set v_status = 'N'
where v_no = #{vNo}

mapper.select:
select count(*)
from v_history
where v_no = #{vNo} and u_no = #{uNo}

select count(*) from volunteer join users using(u_no) join v_main_category using(v_main_category_no) join v_sub_category using(v_sub_category_no) join v_target_category using(v_target_category_no) join v_history using(v_no) where v_history.u_no = #{uNo}
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) >= #{startDate}
and to_date(to_char(v_end_date, 'yyyy-mm-dd')) <= #{endDate}
and v_name like '%${vName}%'
and v_area like '%${vArea}%'
and registrar like '%${registrar}%'
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) > to_date(to_char(sysdate, 'yyyy-mm-dd'))
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) <= to_date(to_char(sysdate, 'yyyy-mm-dd')) and to_date(to_char(v_end_date, 'yyyy-mm-dd')) >= to_date(to_char(sysdate, 'yyyy-mm-dd'))
and to_date(to_char(v_end_date, 'yyyy-mm-dd')) < to_date(to_char(sysdate, 'yyyy-mm-dd'))
order by v_his_no desc

select * from volunteer join users using(u_no) join v_main_category using(v_main_category_no) join v_sub_category using(v_sub_category_no) join v_target_category using(v_target_category_no) join v_history using(v_no) where v_history.u_no = #{uNo}
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) >= #{startDate}
and to_date(to_char(v_end_date, 'yyyy-mm-dd')) <= #{endDate}
and v_name like '%${vName}%'
and v_area like '%${vArea}%'
and registrar like '%${registrar}%'
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) > to_date(to_char(sysdate, 'yyyy-mm-dd'))
and to_date(to_char(v_start_date, 'yyyy-mm-dd')) <= to_date(to_char(sysdate, 'yyyy-mm-dd')) and to_date(to_char(v_end_date, 'yyyy-mm-dd')) >= to_date(to_char(sysdate, 'yyyy-mm-dd'))
and to_date(to_char(v_end_date, 'yyyy-mm-dd')) < to_date(to_char(sysdate, 'yyyy-mm-dd'))
order by v_his_no desc

select * from volunteer join users using(u_no) join v_main_category using(v_main_category_no) join v_sub_category using(v_sub_category_no) join v_target_category using(v_target_category_no)
u_no = #{uNo}
order by v_no desc

mapper.update:
update volunteer
set v_status = #{status}
where v_no = #{vNo}
