mapper.select:
select count(*)
from users
where u_id = #{uId}

select count(*)
from users
where u_NickName = #{uNickName}

mapper.update:
update users
set u_name = #{uName}, u_type = #{uType}, registrar = #{registrar}, resident_no = #{residentNo},
	address = #{address}, phone = #{phone}, email = #{email}, u_nickname = #{uNickName}
where u_id = #{uId}

update users
set u_status = 'N'
where u_id = #{uId}
